/* =============================================================================

    @name           jQuery Input Replacement
    @description    A input text replacement that appends text to input fields 
                    and removes it on focus. Respects prepopulated fields.
    @author         Dana Woodman <dana@danawoodman.com>
    @version        1.1
    @license:       MIT

============================================================================= */

// Minified with http://jscompress.com/ (using JSMin)

(function($){$.fn.input_replacement=function(options){var opts=$.extend({},$.fn.input_replacement.defaults,options);return $(this).each(function(){var obj=$(this);obj.o=$.meta?$.extend({},opts,$this.data()):opts;obj.o.field_text='';if(obj.o.text){obj.o.field_text=obj.o.text;}
else{string=obj.attr('name');string=string.charAt(0).toUpperCase()+string.slice(1);obj.o.field_text=string;};if(obj.val()==''){obj.val(obj.o.field_text);if(obj.o.prefocus_class){obj.addClass(obj.o.prefocus_class);};}
obj.bind('focus',function(){if(obj.val()==obj.o.field_text){obj.val('');};if(obj.o.prefocus_class){obj.removeClass(obj.o.prefocus_class);};});obj.bind('blur',function(){if(obj.val()==''){obj.val(obj.o.field_text);if(obj.o.prefocus_class){obj.addClass(obj.o.prefocus_class);};};});$(window).unload(function(){if(obj.val()==obj.o.field_text){obj.val('');};});var form=obj.parents('form');if(form){form.each(function(){$(form).bind('submit',function(){if(obj.val()==obj.o.field_text){obj.val('');};});});};});};$.fn.input_replacement.defaults={text:'',prefocus_class:''};})(jQuery);